params, _ := util.JsonString(map[string]interface{}{
    "rand": time.Now().Nanosecond(),
})
gobatch.Start(context.Background(), job.Name(), params)

// 或异步运行
// gobatch.StartAsync(context.Background(), job.Name(), "")

